






$('#examples-tabs__slider').owlCarousel({
    items : 3, 
    nav : true,  
    loop : true, 
    margin : 10, 
    navText : ["<i class='fa-solid fa-chevron-left'></i>", "<i class='fa-solid fa-chevron-right'></i>"], 
    responsive : {
        768 : {
            items : 1
        }, 
        992 : {
            items : 1
        }, 
        1200 : {
            items : 3
        }
    }
})

$('.price-tabs__trigger').click(function(){
    $(this).next().slideToggle()
    $(this).find('i').toggleClass('rotate-shevrone')
})